function fetchPlatforms(){$.ajax({url:"/api/v1/platforms",type:"get"}).then(collectPlatforms).then(renderPlatforms)}function createPlatformHTML(t){return $("<div class='platform' data-id='"+t.id+"'><h6><strong>Name:</strong> "+t.name+"</h6><p><strong>Original Price:</strong> "+t.original_price+"</p><p><strong>Units Sold:</strong> "+t.install_base+"</p><p><strong>Total Revenue:</strong> "+t.total_revenue+"</p><p><strong>Release Date:</strong> "+t.release_date+"</p></div>")}function collectPlatforms(t){return t.map(createPlatformHTML)}function renderPlatforms(t){$("#all-platforms").append(t)}$("#all-platforms").ready(function(){fetchPlatforms()});